import { NavLink } from "react-router-dom";

const Nav = () => {
  const lineStyle = {
    fontSize: "40px",
    display: "box",
    padding: "15px",
    borderBottom: "2.5px solid skyblue",
    textAlign: "center",
  };

  const listStyle = {
    listStyleType: "none",
    border: "5px solid skyblue",
    maxWidth: "25%",
    padding: "16px",
    position: "fixed",
  };

  return (
    <>
      <ul style={listStyle}>
        <li style={lineStyle}>
          <NavLink
            key="hero"
            to="/"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            Tristan D'Amours
          </NavLink>
        </li>
        <li style={lineStyle}>
          <NavLink
            key="about"
            to="/about"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            About{" "}
          </NavLink>
        </li>
        <li style={lineStyle}>
          <NavLink
            key="publishedWork"
            to="/publishedwork"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            Published Work{" "}
          </NavLink>
        </li>
        <li style={lineStyle}>
          <NavLink
            key="contact"
            to="/contact"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            Contact{" "}
          </NavLink>
        </li>
      </ul>
    </>
  );
};

export default Nav;
