import styled from "styled-components";

const Hero = () => {
  return (
    <Container className="container">
      <StyledImage
        alt="Tristan D'Amours smiling, arms crossed"
        src={require("../assets/photos/portraitArmsCrossed.png")}
      />
      <StyledParagraph>
        Welcome! My name is Tristan, and I am a bilingual native of Montreal.{" "}
        <br /> I formerly plied my trade as a sports reporter for publications
        such as Canadian Press and Sportsnet. <br /> I now work as a
        communications manager for Major League Soccer team CF Montreal.
      </StyledParagraph>
    </Container>
  );
};

export default Hero;

const Container = styled.div`
  display: flex;
  align-items: center;
  align-content: center;
  justify-content: center;
  max-height: 50%;
  background-color: #108c43;
  border: 5px solid skyblue;
  margin-left: 15px;
  max-width: 50%;
  @media (max-width: 768px) {
    flex-flow: column nowrap;
    max-width: 100%;
    margin-left: 0;
  }
  @media (min-width: 771px) and (max-width: 1664px) {
    flex-flow: column nowrap;
    max-width: 75%;
  }
`;

const StyledImage = styled.img`
  margin: 25px;
  border: 5px solid gold;
  border-radius: 100px;
  background-color: skyblue;
  height: 500px;
  text-align: center;
  @media (max-width: 767px) {
    height: 250px;
  }
  @media (min-width: 777px) and (max-width: 905px) {
    height: 250px;
  }
`;

const StyledParagraph = styled.p`
  fontsize: 25px;
  color: AliceBlue;
  text-align: center;
  padding: 15px;
`;
