import styled from "styled-components";
import { useContext } from "react";
import { LanguageToggleContext } from "./LanguageToggleProvider";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faInstagram,
  faTwitterSquare,
  faMedium,
} from "@fortawesome/free-brands-svg-icons";
import { Header, Title } from "./About";

const Contact = () => {
  const { language } = useContext(LanguageToggleContext);
  return (
    <Container>
      <SubContainer>
        <Header>
          {language === "en" ? "How to follow me: " : "Comment me suivre: "}
        </Header>
        <FontAwesomeIcon
          icon={faInstagram}
          style={{ fontSize: "64px" }}
          color="aliceblue"
          aria-hidden="true"
        />
        <FontAwesomeIcon
          icon={faTwitterSquare}
          style={{ fontSize: "64px" }}
          color="aliceblue"
          aria-hidden="true"
        />
        <FontAwesomeIcon
          icon={faMedium}
          style={{ fontSize: "64px" }}
          color="aliceblue"
          aria-hidden="true"
        />
      </SubContainer>
    </Container>
  );
};

export default Contact;

const Container = styled.div`
  border: 5px solid skyblue;
  border-radius: 50px;
  box-shadow: 20px 20px black;
  display: flex;
  flex-flow: column nowrap;
  align-items: center;
  justify-content: center;
  min-height: 1000px;
  min-width: 1100px;
  max-width: 50%;
`;

const SubContainer = styled.div`
  border: 2.5px solid skyblue;
  border-radius: 50px;
  box-shadow: 15px 15px black;
  display: flex;
  flex-flow: column nowrap;
  align-items: flex-start;
  justify-content: space-evenly;
  min-height: 500px;
  min-width: 1000px;
  max-width: 50%;
  padding: 16px;
`;
