import { NavLink } from "react-router-dom";
import styled from "styled-components";

const Nav = () => {
  const lineStyle = {
    fontSize: "40px",
    display: "box",
    padding: "16px",
    borderBottom: "2.5px dashed skyblue",
    textAlign: "center",
  };

  return (
    <>
      <ul>
        <li>
          <NavLink
            key="hero"
            to="/"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            Tristan D'Amours
          </NavLink>
        </li>
        <li style={lineStyle}>
          <NavLink
            key="about"
            to="/about"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            About{" "}
          </NavLink>
        </li>
        <li style={lineStyle}>
          <NavLink
            key="publishedWork"
            to="/publishedwork"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            Published Work{" "}
          </NavLink>
        </li>
        <li style={lineStyle}>
          <NavLink
            key="contact"
            to="/contact"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            Contact{" "}
          </NavLink>
        </li>
      </ul>
    </>
  );
};

export default Nav;

const StyledList = styled.ul``;
