import { NavLink } from "react-router-dom";
import styled from "styled-components";

const Nav = () => {
  const lineStyle = {
    fontSize: "40px",
    display: "box",
    padding: "16px",
    borderBottom: "2.5px dashed skyblue",
    textAlign: "center",
  };

  const listStyle = {
    listStyleType: "none",
    border: "5px solid skyblue",
    maxWidth: "25%",
    padding: "16px",
    marginTop: "0",
    fontVariant: "small-caps",
  };

  return (
    <>
      <ul>
        <li>
          <NavLink
            key="hero"
            to="/"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            Tristan D'Amours
          </NavLink>
        </li>
        <li style={lineStyle}>
          <NavLink
            key="about"
            to="/about"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            About{" "}
          </NavLink>
        </li>
        <li style={lineStyle}>
          <NavLink
            key="publishedWork"
            to="/publishedwork"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            Published Work{" "}
          </NavLink>
        </li>
        <li style={lineStyle}>
          <NavLink
            key="contact"
            to="/contact"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            Contact{" "}
          </NavLink>
        </li>
      </ul>
    </>
  );
};

export default Nav;

const StyledNav = styled.ul`
  list-style-type: none;
  border: 5px solid skyblue;
  max-width: 25%;
  padding: 16px;
  margin-top: 0;
  font-variant: small-caps;
`;

const StyledLink = styled.li`
  font-size: 40px;
  display: box;
  padding: 16px;
  border-bottom: 2.5px dashed skyblue;
  text-align: center;
`;
