import styled from "styled-components";

const Articles = ({ article }) => {
  const lineStyle = {
    fontSize: "25px",
    display: "box",
    padding: "5px",
    textAlign: "center",
    fontVariant: "small-caps",
    borderBottom: "2.5px dotted skyblue",
  };

  const listStyle = {
    listStyleType: "none",
    border: "2.5px solid gold",
    maxWidth: "25%",
    padding: "16px",
    backgroundColor: "#108c43",
  };

  const linkStyle = {
    color: "AliceBlue",
    textDecoration: "none",
  };

  const dateStyle = {
    color: "rgb(1, 4, 51)",
    fontStyle: "italic",
    marginTop: "10px",
  };

  return (
    <ul style={listStyle}>
      <li style={lineStyle}>
        <a style={linkStyle} href={article.link} target="_blank">
          {article.header}
        </a>
        <div style={dateStyle}>{article.date}</div>
      </li>
    </ul>
  );
};

export default Articles;

const StyledList = styled.ul`
    list-style-type: none;
    border: 2.5px solid gold;
    max-width: 25%;
    padding: 16px;
    background-color: "#108c43;
    @media (max-width: 768px) {
    flex-flow: column nowrap;
  }
`;
