import styled from "styled-components";
import StyledList from "./StyledList";

const About = () => {
  return (
    <>
      <Container>
        <div style={{ width: "2.5%" }}></div>
        <img
          style={{
            backgroundColor: "#108c43",
            border: "15px dashed gold",
            borderRadius: "10px",
            margin: "25px",
          }}
          src={require("../assets/photos/sideline.png")}
        />
        <SubContainer>
          <h1
            style={{
              color: "#108c43",
              fontSize: "50px",
              margin: "0",
              textAlign: "center",
            }}
          >
            Professional Experience
          </h1>
          <StyledList
            title={"Communications manager — CF Montréal (Aug. 2023 - Present)"}
          />
          <StyledList
            title={"Content coordinator — Laval Rocket (June 2023 - Aug. 2023)"}
          />
          <StyledList
            title={"Associate editor — Sportsnet (Jan. 2022 - June 2023)"}
          />
          <StyledList
            title={"Freelance journalist/editor (Sept. 2018 - June 2023)"}
          />
          <StyledList
            title={"Broadcaster — McGill University (Sept. 2021 - Oct. 2022)"}
          />
          <StyledList
            title={
              "Montreal correspondent — Pro Soccer USA (Feb. 2019 - April 2020)"
            }
          />
          <StyledList
            title={"Staff writer — SBI Soccer (Feb. 2016 - Feb. 2018)"}
          />
        </SubContainer>
        <div style={{ width: "2.5%" }}></div>
      </Container>
    </>
  );
};

export default About;

const Container = styled.div`
  border: 5px solid skyblue;
  border-radius: 50px;
  display: flex;
  justify-content: space-between;
  gap: 35px;
`;

const SubContainer = styled.div`
  border: 5px solid skyblue;
  border-radius: 50px;
  display: flex;
  flex-flow: column nowrap;
  justify-content: space-evenly;
  margin: 25px 0;
`;
