import { NavLink } from "react-router-dom";
import { useState } from "react";

const Nav = () => {
  const [isHover, setIsHover] = useState(false);

  const handleMouseEnter = () => {
    setIsHover(true);
  };

  const handleMouseLeave = () => {
    setIsHover(false);
  };

  const navLinkStyle = (isActive) => {
    return {
      color: isActive ? "gold" : "skyblue",
      backgroundColor: isHover ? "#108c43" : null,
      textDecoration: "none",
      fontSize: "40px",
      display: "block",
      padding: "16px",
    };
  };

  return (
    <>
      <ul
        style={{
          listStyleType: "none",
          border: "5px solid",
          position: "fixed",
          width: "300px",
          color: "white",
        }}
      >
        <li
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={navLinkStyle}
        >
          <NavLink key="hero" to="/">
            Tristan D'Amours
          </NavLink>
        </li>
        <li
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={navLinkStyle}
        >
          <NavLink key="about" to="/about">
            About{" "}
          </NavLink>
        </li>
        <li
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={navLinkStyle}
        >
          <NavLink key="publishedWork" to="/publishedwork">
            Published Work{" "}
          </NavLink>
        </li>
        <li
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={navLinkStyle}
        >
          <NavLink key="contact" to="/contact">
            Contact{" "}
          </NavLink>
        </li>
      </ul>
    </>
  );
};

export default Nav;
