import styled from "styled-components";
import { useContext } from "react";
import { LanguageToggleContext } from "./LanguageToggleProvider";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faInstagram,
  faTwitterSquare,
  faMedium,
} from "@fortawesome/free-brands-svg-icons";

const Contact = () => {
  const { language } = useContext(LanguageToggleContext);
  return (
    <Container>
      <div
        style={{
          border: "2.5px solid black",
          borderRadius: "25px",
          boxShadow: "15px 15px black",
          color: "aliceblue",
          fontSize: "75px",
          padding: "16px",
        }}
      >
        CONTACT
      </div>
      <StyledImage
        alt="Tristan D'Amours smiling"
        src={require("../assets/photos/tristanCellphone.jpg")}
      />
      <SubContainer>
        <Header>
          {language === "en" ? "How to follow me: " : "Comment me suivre: "}
        </Header>
        <FontAwesomeIcon
          icon={faInstagram}
          style={{ fontSize: "64px" }}
          color="aliceblue"
          aria-hidden="true"
        />
        <FontAwesomeIcon
          icon={faTwitterSquare}
          style={{ fontSize: "64px" }}
          color="aliceblue"
          aria-hidden="true"
        />
        <FontAwesomeIcon
          icon={faMedium}
          style={{ fontSize: "64px" }}
          color="aliceblue"
          aria-hidden="true"
        />
      </SubContainer>
    </Container>
  );
};

export default Contact;

const Container = styled.div`
  border: 5px solid skyblue;
  border-radius: 50px;
  box-shadow: 20px 20px black;
  display: flex;
  flex-flow: row wrap;
  align-items: center;
  justify-content: space-evenly;
  max-width: 50%;
  padding: 50px;
`;

const SubContainer = styled.div`
  border: 2.5px solid skyblue;
  border-radius: 50px;
  box-shadow: 15px 15px black;
  display: flex;
  flex-flow: column nowrap;
  align-items: center;
  justify-content: space-evenly;
  min-height: 500px;
  min-width: 1000px;
  max-width: 50%;
  padding: 16px;
`;

const Header = styled.div`
  color: skyblue;
  font-size: 28px;
  text-align: center;
  @media (max-width: 768px) {
    font-size: 30px;
  }
  @media (min-width: 770px) and (max-width: 1424px) {
    margin: 15px;
  }
`;

const Title = styled.div`
  color: aliceblue;
  font-size: 22px;
  max-width: 80%;
  text-align: center;
  @media (max-width: 768px) {
    font-size: 22px;
    max-width: 90%;
    margin: 5px;
  }
`;

const StyledImage = styled.img`
  background-color: skyblue;
  border: 15px dashed #108c43;
  box-shadow: 10px 10px gold;
  max-width: 50%;
`;
