import { theLink } from "../assets/data/ScrapedArticles";
import { Container } from "./Hero";
import { useState, useEffect } from "react";

const PublishedWorks = () => {
  const { publication, handlePublication } = useState("");

  const handleSelect = (e) => {
    handlePublication(e.target.value);
  };

  return (
    <div>
      <select>Please select a publication</select>
      <Container style={{ borderRadius: "0" }}>
        {publication === "link" ? (
          theLink.map((article) => {
            return (
              <>
                <div>{article.header}</div>
                <div>{article.date}</div>
                <a href={article.link} />
              </>
            );
          })
        ) : (
          <></>
        )}
      </Container>
    </div>
  );
};

export default PublishedWorks;
