import { Link } from "react-router-dom";
import { useState } from "react";

const Nav = () => {
  const [isHover, setIsHover] = useState(false);

  const handleMouseEnter = () => {
    setIsHover(true);
  };

  const handleMouseLeave = () => {
    setIsHover(false);
  };

  const linkStyle = {
    backgroundColor: isHover ? "#108c43" : null,
    color: "skyblue",
    fontSize: "40px",
    display: "box",
    padding: "16px",
  };

  return (
    <>
      <ul
        style={{
          listStyleType: "none",
          border: "5px solid",
          position: "fixed",
          width: "300px",
          color: "white",
        }}
      >
        <li
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={linkStyle}
        >
          <Link key="hero" to="/" style={{ textDecorationLine: "none" }}>
            Tristan D'Amours
          </Link>
        </li>
        <li
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={linkStyle}
        >
          <Link key="about" to="/about" style={{ textDecorationLine: "none" }}>
            About{" "}
          </Link>
        </li>
        <li
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={linkStyle}
        >
          <Link
            key="publishedWork"
            to="/publishedwork"
            style={{ textDecorationLine: "none" }}
          >
            Published Work{" "}
          </Link>
        </li>
        <li
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={linkStyle}
        >
          <Link
            key="contact"
            to="/contact"
            style={{ textDecorationLine: "none" }}
          >
            Contact{" "}
          </Link>
        </li>
      </ul>
    </>
  );
};

export default Nav;
