import styled from "styled-components";
import { useContext } from "react";
import { LanguageToggleContext } from "./LanguageToggleProvider";

const StyledTitle = ({ englishTitle, frenchTitle }) => {
  const { language } = useContext(LanguageToggleContext);
  return (
    <>
      {language === "en" ? (
        <>
          <Header>{englishHeader}</Header>
          <Title>{englishTitle}</Title>
        </>
      ) : (
        <>
          <Header>{frenchHeader}</Header>
          <Title>{frenchTitle}</Title>
        </>
      )}
    </>
  );
};

export default StyledTitle;

const Header = styled.div`
  color: aliceblue;
  font-size: 18px;
  margin: 2.5%;
  text-align: center;
`;

const Title = styled.div`
  color: aliceblue;
  font-size: 18px;
  margin: 2.5%;
  text-align: center;
`;
