import styled from "styled-components";

const Articles = ({ article }) => {
  // style for each link shown as the article header
  const linkStyle = {
    color: "aliceblue",
    fontSize: "32",
    fontWeight: "bold",
    textDecoration: "none",
  };
  // style for each date from article object
  const dateStyle = {
    color: "black",
    fontSize: "24px",
    fontStyle: "italic",
    margin: "10px",
  };

  return (
    <StyledList>
      <StyledContent>
        <a style={linkStyle} href={article.link} target="_blank">
          {article.header}
        </a>
        <div style={dateStyle}>{article.date}</div>
      </StyledContent>
    </StyledList>
  );
};

export default Articles;

const StyledList = styled.ul`
  display: flex;
  flex-flow: column no wrap;
  justify-content: space-evenly;
  background-color: #108c43;
  border: 2.5px solid gold;
  border-radius: 25px;
  box-shadow: 15px 15px;
  list-style-type: none;
  max-width: 25%;
  min-height: 275px;
  padding: 24px;
  @media (max-width: 460px) {
    box-shadow: 5px 5px;
  }
  @media (max-width: 835px) {
    display: flex;
    justify-content: center;
    max-width: 80%;
  }
  @media (min-width: 1440px) and (max-width: 1530px) {
    padding: 16px;
  }
`;

const StyledContent = styled.li`
  border-bottom: 2.5px dotted skyblue;
  display: box;
  font-size: 25px;
  font-variant: small-caps;
  text-align: center;
  @media (max-width: 768px) {
    max-width: 75%;
  }
`;
