import styled, { keyframes } from "styled-components";
import { useContext, useState, useEffect } from "react";
import { LanguageToggleContext } from "./LanguageToggleProvider";

const Hero = () => {
  const { language } = useContext(LanguageToggleContext);
  const [image, setImage] = useState(false);

  useEffect(() => {
    const triggerFadeIn = () => setImage(true);
    setTimeout(triggerFadeIn, 1000);

    return () => {
      clearTimeout(triggerFadeIn);
    };
  });
  return (
    <Container className="container">
      {image ? (
        <StyledImage
          alt="Tristan D'Amours smiling, arms crossed"
          src={require("../assets/photos/portraitArmsCrossed.png")}
        />
      ) : (
        <></>
      )}
      {language === "en" ? (
        <StyledParagraph>
          Welcome! My name is Tristan, and I am a bilingual native of Montreal.
          I formerly plied my trade as a sports reporter for publications such
          as Canadian Press and Sportsnet. I now work as a communications
          manager for Major League Soccer team CF Montreal.
        </StyledParagraph>
      ) : (
        <StyledParagraph>
          Bienvenue, je m'appelle Tristan ! Je suis né et j'ai grandi à
          Montréal. J'ai exercé mon métier de journaliste sportif pour des
          publications telles que la Presse Canadienne et Sportsnet. Je
          travaille maintenant comme gestionnaire des communications pour
          l'équipe de Major League Soccer, le CF Montréal.
        </StyledParagraph>
      )}
    </Container>
  );
};

export default Hero;

const Container = styled.div`
  display: flex;
  align-items: center;
  align-content: center;
  justify-content: center;
  max-height: 50%;
  background-color: rgb(1, 4, 51);
  border: 5px solid skyblue;
  border-radius: 50px;
  box-shadow: 20px 20px black;
  margin-left: 15px;
  max-width: 50%;
  padding: 24px;
  flex-flow: column nowrap;
  @media (max-width: 768px) {
    box-shadow: 10px 10px black;
    max-width: 100%;
    margin-left: 0;
  }
  @media (min-width: 771px) and (max-width: 1664px) {
    max-width: 75%;
  }
`;

const fadeIn = keyframes`
from { opacity: .25 } to {opacity: 1}
`;

const StyledImage = styled.img`
  animation: 0.5s ${fadeIn} ease-out;
  margin: 25px 0;
  border: 2.5px solid gold;
  border-radius: 100px;
  box-shadow: 15px 15px gold;
  background-color: skyblue;
  height: 500px;
  text-align: center;
  @media (max-width: 767px) {
    box-shadow: 10px 10px gold;
    height: 250px;
  }
  @media (min-width: 777px) and (max-width: 905px) {
    height: 250px;
  }
`;

const StyledParagraph = styled.p`
  background-color: #108c43;
  border: 2.5px solid gold;
  border-radius: 25px;
  box-shadow: 15px 15px black;
  color: AliceBlue;
  font-size: 24px;
  margin: 15px 0;
  max-width: 75%;
  padding: 15px;
  text-align: center;
  @media (max-width: 460px) {
    box-shadow: 10px 10px black;
    margin: 0 0 30px;
  }
`;
