import styled from "styled-components";
import { useContext } from "react";
import { LanguageToggleContext } from "./LanguageToggleProvider";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faInstagram,
  faTwitterSquare,
  faMedium,
} from "@fortawesome/free-brands-svg-icons";

const Contact = () => {
  const { language } = useContext(LanguageToggleContext);
  return (
    <Container>
      <MainTitle>CONTACT</MainTitle>
      <StyledImage
        alt="Tristan D'Amours smiling"
        src={require("../assets/photos/tristanCellphone.jpg")}
      />
      <SubContainer>
        <Header>
          {language === "en" ? "How to follow me: " : "Comment me suivre: "}
        </Header>
        <Section>
          <FontAwesomeIcon
            icon={faInstagram}
            style={{ fontSize: "56px" }}
            color="aliceblue"
            aria-hidden="true"
          />
          <a to="https://www.instagram.com/tristandamours/">tristandamours</a>
        </Section>
        <FontAwesomeIcon
          icon={faTwitterSquare}
          style={{ fontSize: "56px" }}
          color="aliceblue"
          aria-hidden="true"
        />
        <FontAwesomeIcon
          icon={faMedium}
          style={{ fontSize: "56px" }}
          color="aliceblue"
          aria-hidden="true"
        />
      </SubContainer>
    </Container>
  );
};

export default Contact;

const Container = styled.div`
  border: 5px solid skyblue;
  border-radius: 50px;
  box-shadow: 20px 20px black;
  display: flex;
  flex-flow: row wrap;
  align-items: center;
  justify-content: space-evenly;
  max-width: 50%;
  padding: 16px;
  @media (max-width: 768px) {
    box-shadow: 10px 10px black;
    flex-flow: column nowrap;
    max-width: 100%;
  }
  @media (min-width: 777px) and (max-width: 1280px) {
    max-width: 75%;
  }
`;

const SubContainer = styled.div`
  border: 2.5px solid skyblue;
  border-radius: 50px;
  box-shadow: 15px 15px black;
  display: flex;
  flex-flow: column nowrap;
  align-items: center;
  justify-content: space-evenly;
  max-width: 50%;
  margin-bottom: 50px;
  min-height: 500px;
  min-width: 85%;
  padding: 16px;
  @media (max-width: 768px) {
    box-shadow: 10px 10px black;
    max-width: 100%;
  }
`;

const MainTitle = styled.div`
  border: 2.5px solid black;
  border-radius: 25px;
  box-shadow: 15px 15px black;
  color: aliceblue;
  font-size: 75px;
  padding: 16px;
  @media (max-width: 768px) {
    box-shadow: 7.5px 7.5px black;
    font-size: 45px;
    margin-top: 25px;
  }
`;

const Header = styled.div`
  color: skyblue;
  font-size: 32px;
  text-align: center;
  @media (max-width: 768px) {
    font-size: 26px;
  }
  @media (min-width: 770px) and (max-width: 1424px) {
    margin: 15px;
  }
`;

const Section = styled.div`
  border: 2.55px solid skyblue;
  border-radius: 25px;
  display: flex;
  align-items: center;
  justify-content: space-evenly;
  gap: 25px;
  max-width: 100%;
  padding: 16px;
`;

const Link = styled.a`
  color: aliceblue;
  font-size: 28px;
  max-width: 80%;
  text-align: center;
  @media (max-width: 768px) {
    font-size: 22px;
    max-width: 90%;
    margin: 5px;
  }
`;

const StyledImage = styled.img`
  background-color: gold;
  border: 15px dashed #108c43;
  border-radius: 50px;
  box-shadow: 15px 15px black;
  margin: 50px;
  max-width: 50%;
  @media (max-width: 768px) {
    border: 7.5px dashed #108c43;
    box-shadow: 10px 10px black;
    margin: 25px;
    max-width: 85%;
  }
`;
