import { theLink } from "../assets/data/ScrapedArticles";
import { Container } from "./Hero";
import { useState, useEffect } from "react";

const PublishedWorks = () => {
  const [publication, setPublication] = useState("");

  const handleSelect = (e) => {
    setPublication(e.target.value);
  };

  return (
    <div>
      <Container style={{ borderRadius: "0", flexFlow: "column nowrap" }}>
        <select onClick={handleSelect} id="publicationSelect">
          <option value="link">The Link Newspaper</option>
        </select>
        {publication === "link" ? (
          theLink.map((article) => {
            return (
              <>
                <a href={article.link} target="_blank"></a>
                <div>{article.header}</div>
                <div>{article.date}</div>
              </>
            );
          })
        ) : (
          <></>
        )}
      </Container>
    </div>
  );
};

export default PublishedWorks;
