import portraitArmsCrossed from "../assets/photos/portraitArmsCrossed.png";
import styled from "styled-components";

const Hero = () => {
  const imageStyle = {
    margin: "25px",
    border: "5px solid gold",
    borderRadius: "100px",
    backgroundColor: "skyblue",
    height: "500px",
  };

  return (
    <Container>
      <img
        alt="'Tristan D'Amours smiling, arms crossed"
        src={portraitArmsCrossed}
        style={imageStyle}
      />
      <p
        style={{
          fontSize: "25px",
          color: "AliceBlue",
          padding: "20px",
          textAlign: "center",
        }}
      >
        My name is Tristan, and I am a bilingual Montreal native. <br />{" "}
        Formerly a sports reporter for publications such as Canadian Press and
        Sportsnet, I now work for CF Montreal's media relations department.
      </p>
    </Container>
  );
};

export default Hero;

export const Container = styled.div`
  border: 5px solid skyblue;
  background-color: #108c43;
  max-width: 50%;
  height: 600px;
  margin-left: 15px;
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
`;
