import NavLink from "./NavLink";
import styled from "styled-components";

const Nav = () => {
  return (
    <>
      <StyledNav>
        <StyledLink>
          <NavLink
            key="hero"
            to="/"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            Tristan D'Amours
          </NavLink>
        </StyledLink>
        <StyledLink>
          <NavLink
            key="about"
            to="/about"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            About{" "}
          </NavLink>
        </StyledLink>
        <StyledLink>
          <NavLink
            key="publishedWork"
            to="/publishedwork"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            Published Work{" "}
          </NavLink>
        </StyledLink>
        <StyledLink>
          <NavLink
            key="contact"
            to="/contact"
            style={({ isActive }) => {
              return {
                textDecorationLine: "none",
                color: isActive ? "gold" : "#108c43",
              };
            }}
          >
            Contact{" "}
          </NavLink>
        </StyledLink>
      </StyledNav>
    </>
  );
};

export default Nav;

const StyledNav = styled.ul`
  border: 5px solid skyblue;
  font-variant: small-caps;
  list-style-type: none;
  margin-top: 0;
  margin-right: 30px;
  max-width: 25%;
  padding: 16px;
  @media (max-width: 768px) {
    max-width: 100%;
    margin-right: 0;
  }
`;

const StyledLink = styled.li`
  border-bottom: 2.5px dashed skyblue;
  display: box;
  font-size: 40px;
  padding: 16px;
  text-align: center;
`;
