import styled from "styled-components";
// import portraitArmsCrossed from "./photos/";

const Hero = () => {
  const imageStyle = {
    margin: "25px",
    border: "5px solid gold",
    borderRadius: "100px",
    backgroundColor: "skyblue",
    height: "500px",
    textAlign: "center",
  };

  return (
    <Container className="container">
      <img
        alt="Tristan D'Amours smiling, arms crossed"
        src={"../assets/photos/portraitArmsCrossed.png"}
        style={imageStyle}
      />
      <p
        style={{
          fontSize: "25px",
          color: "AliceBlue",
          padding: "20px",
          textAlign: "center",
        }}
      >
        Welcome! My name is Tristan, and I am a bilingual native of Montreal.{" "}
        <br /> I formerly plied my trade as a sports reporter for publications
        such as Canadian Press and Sportsnet. I now work as a communications
        manager for Major League Soccer team CF Montreal.
      </p>
    </Container>
  );
};

export default Hero;

const Container = styled.div`
  display: flex;
  align-items: center;
  align-content: center;
  justify-content: center;
  height: 650px;
  background-color: #108c43;
  border: 5px solid skyblue;
  margin-left: 15px;
  max-width: 50%;
  @media (max-width: 768px) {
    flex-flow: column nowrap;
    max-width: 100%;
    margin-left: 0;
  }
`;
