import StyledNavLink from "./StyledNavLink";
import styled from "styled-components";

const Nav = () => {
  return (
    <>
      <StyledNav>
        <StyledLine>
          <StyledNavLink
            link={"/"}
            englishContent={"Tristan D'Amours"}
            frenchContent={"Tristan D'Amours"}
          />
        </StyledLine>
        <StyledLine>
          <StyledNavLink
            link={"/about"}
            englishContent={"About me"}
            frenchContent={"À propos de moi"}
          />
        </StyledLine>
        <StyledLine>
          <StyledNavLink
            link={"/publishedwork"}
            englishContent={"Published Work"}
            frenchContent={"Rédaction"}
          />
        </StyledLine>
        <StyledLine>
          <StyledNavLink
            link={"/contact"}
            englishContent={"Contact"}
            frenchContent={"Contact"}
          />
        </StyledLine>
        <ToggleContainer>
          <ToggleButton onClick={handleEnglish}>EN</ToggleButton>
          <ToggleButton onClick={handleFrench}>FR</ToggleButton>
        </ToggleContainer>
      </StyledNav>
    </>
  );
};

export default Nav;

const StyledNav = styled.ul`
  border: 5px solid skyblue;
  border-radius: 50px;
  font-variant: small-caps;
  list-style-type: none;
  margin-top: 0;
  margin-right: 30px;
  max-width: 25%;
  padding: 32px;
  @media (max-width: 768px) {
    max-width: 100%;
    margin-right: 0;
  }
`;

const StyledLine = styled.li`
  border-bottom: 2.5px dashed skyblue;
  display: box;
  font-size: 40px;
  padding: 16px;
  text-align: center;
  @media (max-width: 768px) {
    font-size: 30px;
  }
`;

const ToggleContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 25px;
`;

const ToggleButton = styled.button`
  background-color: rgb(1, 4, 51);
  border-radius: 5px;
  color: AliceBlue;
  font-size: 20px;
  margin-top: 25px;
  padding: 16px;
`;
