import { theLink, sportsnet } from "../assets/data/articles";
import { useState } from "react";
import styled from "styled-components";

const PublishedWorks = () => {
  const [publication, setPublication] = useState("link");

  const handleSelect = (e) => {
    setPublication(e.target.value);
  };

  const selectBarStyle = {
    padding: "5px",
    margin: "25px 0",
    color: "rgb(1, 4, 51)",
    backgroundColor: "AliceBlue",
    fontSize: "20px",
    fontVariant: "small-caps",
    fontWeight: "bold",
  };

  const lineStyle = {
    fontSize: "25px",
    display: "box",
    padding: "5px",
    textAlign: "center",
    fontVariant: "small-caps",
    borderBottom: "2.5px dotted skyblue",
  };

  const listStyle = {
    listStyleType: "none",
    border: "2.5px solid gold",
    maxWidth: "25%",
    padding: "16px",
    backgroundColor: "#108c43",
  };

  const linkStyle = {
    color: "AliceBlue",
    textDecoration: "none",
  };

  const dateStyle = {
    color: "rgb(1, 4, 51)",
    fontStyle: "italic",
    marginTop: "10px",
  };

  return (
    <Container>
      <select
        style={selectBarStyle}
        onClick={handleSelect}
        id="publicationSelect"
      >
        <option value="link">The Link Newspaper</option>
        <option value="sportsnet">Sportsnet</option>
      </select>
      <SubContainer>
        {publication === "link" ? (
          theLink.map((article) => {
            return (
              <ul style={listStyle}>
                <li style={lineStyle}>
                  <a style={linkStyle} href={article.link} target="_blank">
                    {article.header}
                  </a>
                  <div style={dateStyle}>{article.date}</div>
                </li>
              </ul>
            );
          })
        ) : (
          <></>
        )}
        {publication === "sportsnet" ? (
          sportsnet.map((article) => {
            return (
              <ul style={listStyle}>
                <li style={lineStyle}>
                  <a style={linkStyle} href={article.link} target="_blank">
                    {article.header}
                  </a>
                  <div style={dateStyle}>{article.date}</div>
                </li>
              </ul>
            );
          })
        ) : (
          <></>
        )}
      </SubContainer>
    </Container>
  );
};

export default PublishedWorks;

const Container = styled.div`
  align-items: center;
  border: 5px solid skyblue;
  display: flex;
  flex-flow: column nowrap;
  max-width: 50%;
`;

const SubContainer = styled.div`
  text-align: center;
  font-size: 20px;
  display: flex;
  flex-flow: row wrap;
  justify-content: center;
  gap: 10px;
`;
