import { NavLink } from "react-router-dom";
import { useState } from "react";

const Nav = () => {
  const [isHover, setIsHover] = useState(false);

  const handleMouseEnter = () => {
    setIsHover(true);
  };

  const handleMouseLeave = () => {
    setIsHover(false);
  };

  return (
    <>
      <ul
        style={{
          listStyleType: "none",
          border: "5px solid",
          color: "white",
        }}
      >
        <li>
          <NavLink
            key="hero"
            to="/"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={({ isActive }) => {
              return {
                color: isActive ? "gold" : "skyblue",
                backgroundColor: isHover ? "#108c43" : null,
                textDecoration: "none",
                fontSize: "40px",
                display: "block",
              };
            }}
          >
            Tristan D'Amours
          </NavLink>
        </li>
        <li>
          <NavLink
            key="about"
            to="/about"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={({ isActive }) => {
              return {
                color: isActive ? "gold" : "skyblue",
                textDecoration: "none",
                fontSize: "35px",
                display: "block",
              };
            }}
          >
            About{" "}
          </NavLink>
        </li>
        <li>
          <NavLink
            key="publishedWork"
            to="/publishedwork"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={({ isActive }) => {
              return {
                color: isActive ? "gold" : "skyblue",
                textDecoration: "none",
                fontSize: "35px",
                display: "block",
              };
            }}
          >
            Published Work{" "}
          </NavLink>
        </li>
        <li>
          <NavLink
            key="contact"
            to="/contact"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={({ isActive }) => {
              return {
                color: isActive ? "gold" : "skyblue",
                textDecoration: "none",
                fontSize: "35px",
                display: "block",
              };
            }}
          >
            Contact{" "}
          </NavLink>
        </li>
      </ul>
    </>
  );
};

export default Nav;
