import { Link } from "react-router-dom";
import { useState } from "react";
import styled from "styled-components";

const Nav = () => {
  const [isHover, setIsHover] = useState(false);

  const handleMouseEnter = () => {
    setIsHover(true);
  };

  const handleMouseLeave = () => {
    setIsHover(false);
  };

  return (
    <>
      <ul
        style={{
          listStyleType: "none",
          border: "5px solid",
          position: "fixed",
          width: "300px",
          color: "white",
        }}
      >
        <li
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={{
            backgroundColor: isHover ? "#108c43" : "",
          }}
        >
          <Link key="hero" to="/">
            Tristan D'Amours
          </Link>
        </li>
        <li
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={{
            backgroundColor: isHover ? "#108c43" : "",
          }}
        >
          <Link key="about" to="/about">
            About{" "}
          </Link>
        </li>
        <li
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={{
            backgroundColor: isHover ? "#108c43" : "",
          }}
        >
          <Link key="publishedWork" to="/publishedwork">
            Published Work{" "}
          </Link>
        </li>
        <li
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={{
            backgroundColor: isHover ? "#108c43" : "",
          }}
        >
          <Link key="contact" to="/contact">
            Contact{" "}
          </Link>
        </li>
      </ul>
    </>
  );
};

export default Nav;
