import styled from "styled-components";

const About = () => {
  return (
    <>
      <Container>
        Professional experience: Communications Manager — CF Montréal (Aug. 2023
        - Present) Main point of contact for interviews and media requests
        regarding CF Montréal first team players and coaching staff. Content
        coordinator — Laval Rocket (June 2023 - Aug. 2023) Content creation for
        Laval Rocket in-arena entertainment and marketing email communications.
        Associate Editor — Sportsnet (Jan. 2022 - June 2023) Writing news
        pieces, editing articles from Sportsnet writers and columnists, drafting
        social media posts for writer/columnist stories as well as maintaining
        and updating the Sportsnet website pages. Freelance journalist/editor
        (Sept. 2018 - June 2023) NHL and CFL coverage for The Canadian Press
        (Aug. 2021 - June 2023) Major League Soccer coverage for MLSSoccer.com,
        the official website of MLS (June 2019 - June 2022) Articles for Orlando
        Sentinel, Chicago Tribune, Al Jazeera, Howler Magazine, fr.canpl.ca and
        canpl.ca Radio appearances on TSN 690 AM, TSN 1260 AM and BPM Sports
        (French). Broadcaster — McGill University (Sept. 2021 - Oct. 2022)
        Play-by-play announcing for the McGill Redbirds men’s soccer program.
        Colour commentary for the McGill Martlets women’s soccer program.
        Montreal Correspondent — Pro Soccer USA (Feb. 2019 - April 2020) Beat
        coverage of the Montreal Impact (now CF Montréal) for Pro Soccer USA, an
        online startup venture from the Orlando Sentinel. Staff writer — SBI
        Soccer (Feb. 2016 - Feb. 2018) Major League Soccer coverage from
        Montreal for an American audience. Education: Bachelor of Arts,
        Journalism — Concordia University (2019) Recipient of the Rogers Media
        Scholarship (2016) DEC, Liberal Arts (CEGEP) — Champlain College - St.
        Lambert (2014)
      </Container>
    </>
  );
};

export default About;

const Container = styled.div`
  border: 5px solid skyblue;
  border-radius: 50px;
  margin-left: 15px;
  max-width: 50%;
`;
