import styled from "styled-components";
import StyledContent from "./StyledContent";

const About = () => {
  return (
    <>
      <Container>
        <div style={{ width: "2.5%" }}></div>
        <img
          style={{
            backgroundColor: "#108c43",
            border: "15px dashed gold",
            borderRadius: "10px",
            margin: "25px",
          }}
          src={require("../assets/photos/sideline.png")}
        />
        <SubContainer>
          <StyledContent
            englishTitle={
              "Communications manager — CF Montréal (Aug. 2023 - Present)"
            }
            frenchTitle={
              "Gestionnaire des communications - CF Montréal (août 2023 - aujourd'hui)"
            }
          />
          <StyledContent
            englishTitle={
              "Content coordinator — Laval Rocket (June 2023 - Aug. 2023)"
            }
          />
          <StyledContent
            englishTitle={
              "Associate editor — Sportsnet (Jan. 2022 - June 2023)"
            }
          />
          <StyledContent
            englishTitle={
              "Freelance journalist/editor (Sept. 2018 - June 2023)"
            }
          />
          <StyledContent
            englishTitle={
              "Broadcaster — McGill University (Sept. 2021 - Oct. 2022)"
            }
          />
          <StyledContent
            englishTitle={
              "Montreal correspondent — Pro Soccer USA (Feb. 2019 - April 2020)"
            }
          />
          <StyledContent
            englishTitle={"Staff writer — SBI Soccer (Feb. 2016 - Feb. 2018)"}
          />
        </SubContainer>
        <div style={{ width: "2.5%" }}></div>
      </Container>
    </>
  );
};

export default About;

const Container = styled.div`
  border: 5px solid skyblue;
  border-radius: 50px;
  display: flex;
  justify-content: space-between;
  gap: 50px;
`;

const SubContainer = styled.div`
  border: 5px solid skyblue;
  border-radius: 50px;
  display: flex;
  flex-flow: column nowrap;
  justify-content: space-evenly;
  margin: 25px 0;
`;
