import styled from "styled-components";

const SelectPubContainer = ({ language, setPublication }) => {
  const handleSelect = (e) => {
    setPublication(e.target.value);

    return (
      <SelectContainer>
        <SelectLabel>
          {language === "en"
            ? "Select a publication: "
            : "Sélectionner une publication: "}
        </SelectLabel>
        <SelectBar onClick={handleSelect} id="publicationSelect">
          <option value="cp">Canadian Press</option>
          <option value="free">Freelance</option>
          <option value="link">The Link Newspaper</option>
          <option value="mlssoccer">mlssoccer.com</option>
          <option value="sportsnet">Sportsnet</option>
        </SelectBar>
      </SelectContainer>
    );
  };
};

export default SelectPubContainer;

const SelectContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 25px 0;
  border: 2.5px solid black;
  border-radius: 25px;
  box-shadow: 10px 10px black;
  padding: 16px;
  @media (max-width: 768px) {
    align-items: center;
    flex-flow: column nowrap;
    margin: 15px;
    padding: 8px;
  }
`;

const SelectBar = styled.select`
  background-color: AliceBlue;
  border-radius: 12.5px;
  color: rgb(1, 4, 51);
  font-size: 25px;
  font-variant: small-caps;
  padding: 5px;
  text-align: center;
  @media (max-width: 768px) {
    font-size: 15px;
    padding: 8px;
  }
`;

const SelectLabel = styled.label`
  color: gold;
  font-size: 30px;
  font-variant: small-caps;
  margin-right: 10px;
  text-align: center;
  @media (max-width: 768px) {
    font-size: 18px;
    margin-right: 0;
    margin-bottom: 10px;
  }
`;
