import StyledNavLink from "./StyledNavLink";
import styled from "styled-components";
import { useContext } from "react";
import { LanguageToggleContext } from "./LanguageToggleProvider";

const Nav = () => {
  const { language, handleFrench, handleEnglish } = useContext(
    LanguageToggleContext
  );

  return;
  {
    language === "en" ? (
      <>
        <StyledNav>
          <StyledLink>
            <StyledNavLink link={"/"} content={"Tristan D'Amours"} />
          </StyledLink>
          <StyledLink>
            <StyledNavLink link={"/about"} content={"About"} />
          </StyledLink>
          <StyledLink>
            <StyledNavLink link={"/publishedwork"} content={"Published Work"} />
          </StyledLink>
          <StyledLink>
            <StyledNavLink link={"/contact"} content={"Contact"} />
          </StyledLink>
          <ToggleContainer>
            <ToggleButton onClick={handleEnglish}>EN</ToggleButton>
            <ToggleButton>|</ToggleButton>
            <ToggleButton onClick={handleFrench}>FR</ToggleButton>
          </ToggleContainer>
        </StyledNav>
      </>
    ) : (
      <></>
    );
  }
};

export default Nav;

const StyledNav = styled.ul`
  border: 5px solid skyblue;
  border-radius: 50px;
  font-variant: small-caps;
  list-style-type: none;
  margin-top: 0;
  margin-right: 30px;
  max-width: 25%;
  padding: 32px;
  @media (max-width: 768px) {
    max-width: 100%;
    margin-right: 0;
  }
`;

const StyledLink = styled.li`
  border-bottom: 2.5px dashed skyblue;
  display: box;
  font-size: 40px;
  padding: 16px;
  text-align: center;
  @media (max-width: 768px) {
    font-size: 30px;
  }
`;

const ToggleContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ToggleButton = styled.div`
  color: AliceBlue;
  font-size: 20px;
  padding: 16px;
`;
