import styled from "styled-components";
// import portraitArmsCrossed from "./photos/";

const Hero = () => {
  const imageStyle = {
    margin: "25px",
    border: "5px solid gold",
    borderRadius: "100px",
    backgroundColor: "skyblue",
    height: "500px",
  };

  return (
    <Container className="container">
      <img
        alt="'Tristan D'Amours smiling, arms crossed"
        src={"../assets/photos/portraitArmsCrossed.png"}
        style={imageStyle}
      />
      <p
        style={{
          fontSize: "25px",
          color: "AliceBlue",
          padding: "20px",
          textAlign: "center",
        }}
      >
        Hey, my name is Tristan, I am a bilingual native of Montreal. <br /> I
        formerly plied my trade as a sports reporter for publications such as
        Canadian Press and Sportsnet. I now work as a communications manager for
        CF Montreal.
      </p>
    </Container>
  );
};

export default Hero;

const Container = styled.div`
  border: 5px solid skyblue;
  background-color: #108c43;
  max-width: 50%;
  height: 650px;
  margin-left: 15px;
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
`;
