import { useState } from "react";
import styled from "styled-components";
import Articles from "./Articles";
import SelectPubContainer from "./SelectPubContainer";
import {
  canadianPress,
  freelance,
  theLink,
  sportsnet,
  mlssoccer,
} from "../assets/data/articles";

const PublishedWorks = () => {
  const [publication, setPublication] = useState("cp");

  return (
    <Container>
      <SelectPubContainer setPublication={setPublication} />
      <SubContainer>
        {publication === "cp" ? (
          canadianPress.map((article) => {
            return <Articles article={article} />;
          })
        ) : (
          <></>
        )}
        {publication === "free" ? (
          freelance.map((article) => {
            return <Articles article={article} />;
          })
        ) : (
          <></>
        )}
        {publication === "link" ? (
          theLink.map((article) => {
            return <Articles article={article} />;
          })
        ) : (
          <></>
        )}
        {publication === "sportsnet" ? (
          sportsnet.map((article) => {
            return <Articles article={article} />;
          })
        ) : (
          <></>
        )}
        {publication === "mlssoccer" ? (
          mlssoccer.map((article) => {
            return <Articles article={article} />;
          })
        ) : (
          <></>
        )}
      </SubContainer>
    </Container>
  );
};

export default PublishedWorks;

const Container = styled.div`
  display: flex;
  flex-flow: column nowrap;
  align-items: center;
  border: 5px solid skyblue;
  border-radius: 50px;
  box-shadow: 20px 20px black;
  max-width: 50%;
  padding: 16px;
  @media (max-width: 768px) {
    box-shadow: 10px 10px black;
    flex-flow: column nowrap;
    margin-left: 0;
    max-width: 95%;
    padding: 0;
  }
  @media (min-width: 777px) and (max-width: 1280px) {
    max-width: 75%;
  }
`;

const SubContainer = styled.div`
  display: flex;
  flex-flow: row wrap;
  justify-content: space-evenly;
  align-items: center;
  align-content: center;
  text-align: center;
  @media (max-width: 768px) {
    align-items: center;
    flex-flow: column nowrap;
  }
`;
