import { useState } from "react";
import styled from "styled-components";
import Articles from "./Articles";
import { theLink, sportsnet, mlssoccer } from "../assets/data/articles";

const PublishedWorks = () => {
  const [publication, setPublication] = useState("link");

  const handleSelect = (e) => {
    setPublication(e.target.value);
  };

  const selectBarStyle = {
    padding: "5px",
    margin: "25px 0",
    color: "rgb(1, 4, 51)",
    backgroundColor: "AliceBlue",
    fontSize: "25px",
    fontVariant: "small-caps",
    textAlign: "center",
  };

  return (
    <Container>
      <select
        style={selectBarStyle}
        onClick={handleSelect}
        id="publicationSelect"
      >
        <option value="link">The Link Newspaper</option>
        <option value="mlssoccer">mlssoccer.com</option>
        <option value="sportsnet">Sportsnet</option>
      </select>
      <SubContainer>
        {publication === "link" ? (
          theLink.map((article) => {
            return <Articles article={article} />;
          })
        ) : (
          <></>
        )}
        {publication === "sportsnet" ? (
          sportsnet.map((article) => {
            return <Articles article={article} />;
          })
        ) : (
          <></>
        )}
        {publication === "mlssoccer" ? (
          mlssoccer.map((article) => {
            return <Articles article={article} />;
          })
        ) : (
          <></>
        )}
      </SubContainer>
    </Container>
  );
};

export default PublishedWorks;

const Container = styled.div`
  align-items: center;
  border: 5px solid skyblue;
  display: flex;
  flex-flow: column nowrap;
  max-width: 50%;
  @media (max-width: 768px) {
    flex-flow: column nowrap;
    max-width: 100%;
  }
`;

const SubContainer = styled.div`
  text-align: center;
  font-size: 25px;
  display: flex;
  flex-flow: row wrap;
  justify-content: center;
  gap: 10px;
  @media (max-width: 768px) {
    flex-flow: column nowrap;
  }
`;
