import styled from "styled-components";

const Articles = ({ article }) => {
  const linkStyle = {
    color: "AliceBlue",
    textDecoration: "none",
  };

  const dateStyle = {
    color: "rgb(1, 4, 51)",
    fontStyle: "italic",
    margin: "1opx 0 10px 0",
  };

  return (
    <StyledList>
      <StyledContent>
        <a style={linkStyle} href={article.link} target="_blank">
          {article.header}
        </a>
        <div style={dateStyle}>{article.date}</div>
      </StyledContent>
    </StyledList>
  );
};

export default Articles;

const StyledList = styled.ul`
  background-color: #108c43;
  border: 2.5px solid gold;
  list-style-type: none;
  max-width: 25%;
  padding: 16px;
  @media (max-width: 835px) {
    display: flex;
    justify-content: center;
    max-width: 80%;
  }
`;

const StyledContent = styled.li`
  border-bottom: 2.5px dotted skyblue;
  display: box;
  font-size: 25px;
  font-variant: small-caps;
  text-align: center;
  @media (max-width: 768px) {
    max-width: 75%;
  }
`;
