import { theLink } from "../assets/data/ScrapedArticles";
import styled from "styled-components";
import { useState, useEffect } from "react";

const PublishedWorks = () => {
  const [publication, setPublication] = useState("");

  const handleSelect = (e) => {
    setPublication(e.target.value);
  };

  const selectStyle = {
    fontSize: "25px",
  };

  return (
    <MainContainer>
      <select style={selectStyle} onClick={handleSelect} id="publicationSelect">
        <option value="">Select a publication</option>
        <option value="link">The Link Newspaper</option>
      </select>
      <div>
        <br />
        {publication === "link" ? (
          theLink.map((article) => {
            return (
              <>
                <a href={article.link} target="_blank">
                  {article.header}
                </a>
                <div>{article.date}</div>
              </>
            );
          })
        ) : (
          <></>
        )}
      </div>
    </MainContainer>
  );
};

export default PublishedWorks;

const MainContainer = styled.div`
  border: 5px solid gold;
  background-color: #108c43;
  font-size: 25px;
  text-align: center;
`;
