import { NavLink } from "react-router-dom";

const Nav = () => {
  const linkStyle = {
    fontSize: "40px",
    display: "box",
    padding: "16px",
  };

  return (
    <>
      <ul
        style={{
          listStyleType: "none",
          border: "5px solid",
          position: "fixed",
          width: "300px",
          color: "white",
        }}
      >
        <li style={linkStyle}>
          <NavLink
            key="hero"
            to="/"
            style={({ isActive }) => {
              return {
                color: isActive ? "gold" : "",
                textDecorationLine: "none",
                color: "#108c43",
              };
            }}
          >
            Tristan D'Amours
          </NavLink>
        </li>
        <li style={linkStyle}>
          <NavLink
            key="about"
            to="/about"
            style={({ isActive }) => {
              return {
                color: isActive ? "gold" : "",
                textDecorationLine: "none",
                color: "#108c43",
              };
            }}
          >
            About{" "}
          </NavLink>
        </li>
        <li style={linkStyle}>
          <NavLink
            key="publishedWork"
            to="/publishedwork"
            style={({ isActive }) => {
              return {
                color: isActive ? "gold" : "",
                textDecorationLine: "none",
                color: "#108c43",
              };
            }}
          >
            Published Work{" "}
          </NavLink>
        </li>
        <li style={linkStyle}>
          <NavLink
            key="contact"
            to="/contact"
            style={({ isActive }) => {
              return {
                color: isActive ? "gold" : "",
                textDecorationLine: "none",
                color: "#108c43",
              };
            }}
          >
            Contact{" "}
          </NavLink>
        </li>
      </ul>
    </>
  );
};

export default Nav;
